import styled from 'styled-components';
import Movie from './components/Movie';

const Container = styled.div`
  dispaly: flex;
  flex-direction: column;
  `;
const Header = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  background-color: black;
  color: white;
  padding: 10px;
  font-size: 25px;
  font-weight: bold;
  box-shadow: 0 3px 6px 0 #555;
`;
const AppName = styled.div`
  dispaly: flex;
  flex-direction: row;
  align-items: center;
`;
const MovieImage = styled.img`
  width: 48px;
  height: 48px;
  margin: 15px;
  `;
const SearchBox = styled.div`
  dispaly: flex;
  flex-direction: row;
  padding: 10px;
  background-color: white;
  border-radius: 6px;
  margin-left: 20px;
  width: 50%;
  background-color: white;
  align-items: center;
  `;
const SearchIcon = styled.img`
  width: 32px;
  height: 32px;
  margin-top: 25px;
  `;
const SearchInput = styled.input`
  color: white;
  font-size: 16px;
  font-weight: bold;
  border: none;
  outline: none;
  margin-left: 15px;
  `;
const MovieListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
  justify-content: space-between
  `;


function App() {
  return (
    <Container >
      <Header>
        <AppName>
          <MovieImage src='/movie-icon.png'/>
          The Movie Search App
        </AppName> 
        <SearchBox>
          <SearchIcon src='/search-icon1.png'/>
          <SearchInput placeholder='Search Movie'/>
        </SearchBox>
      </Header>
      <MovieListContainer>
        <Movie />
        <Movie />
        <Movie />
        <Movie />
      </MovieListContainer>
    </Container>
  );
}

export default App;
