import React from 'react'
import styled from 'styled-components'

const MovieContainer = styled.div`
    dispaly: flex;
    flex-direction: column; 
    padding: 10px;
    width: 280px;
    box-shadow: 0 3px 10px 0 #aaa;
    `;
const MovieName =styled.span`
    font-size: 18px;
    font-weight: 600;
    color: black;
    margin: 15px;
    // white-space: nowrap;
    overflow: hidden;
    padding: 10px 5px;
    text-overflow: ellipsis;
    `
const CoverImage = styled.img`
    object-fit: cover;  
    height: 362px;
    `;
const InfoColumn = styled.div`
    dispaly: flex;
    flex-direction: row;
    justify-content: space-between;
    `
const MovieInfo = styled.span`
    font-size; 16px;
    font-weight: 500;
    color: black;
    margin: 5px 20px;
    text-transform: capitalize;
  `;

const Movie = (props) => {
  return (
    <MovieContainer>
        <CoverImage src='https://images.unsplash.com/photo-1590179068383-b9c69aacebd3?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80'/>
        <MovieName>The gurdian Body hidden in the Heaven</MovieName>
        <InfoColumn>
            <MovieInfo>Year: 2020</MovieInfo>
            <MovieInfo>Type: Movie</MovieInfo>
        </InfoColumn>
    </MovieContainer>
  )
}

export default Movie
