import React from 'react'
import styled from 'styled-components'

const MovieContainer = styled.div`
    dispaly: flex;
    flex-direction: column; 
    padding: 10px;
    width: 280px;
    box-shadow: 0 3px 10px 0 #aaa;
    `;
const MovieName =styled.span`
    font-size: 18px;
    font-weight: 600;
    color: black;
    margin: 15px;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    `
const CoverImage = styled.img`
    object-fit: cover;  
    height: 362px;
    `

const Movie = (props) => {
  return (
    <MovieContainer>
        <CoverImage src='https://images.unsplash.com/photo-1590179068383-b9c69aacebd3?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80'/>
        <MovieName>Movie The gurdian Body hidden in the Heaven</MovieName><MovieName/>
    </MovieContainer>
  )
}

export default Movie
