import styled from 'styled-components';

const Container = styled.div`
  dispaly: flex;
  flex-direction: column;
  `;
const Header = styled.div`
  display: flex;
  flex-direction: row;
  background-color: black;
  color: white;
  padding: 10px;
  font-size: 25px;
  font-weight: bold;
  box-shadow: 0 3px 6px 0 #555;
`;
const AppName = styled.div`
  dispaly: flex;
  flex-direction: row;
  align-items: center;
`;
const MovieImage = styled.img`
  width: 48px;
  height: 48px;
  margin: 15px;
  `;
const SearchBox = styled.div`
  dispaly: flex;
  flex-direction: row;
  padding: 10px 10px;
  background-color: white;
  border-radius: 6px;
  margin-left: 20px;
  width: 50px;
  background-color: white;
  `;
const SearchIcon = styled.img`
  width: 32px;
  height: 32px;
  `;


function App() {
  return (
    <Container >
      <Header>
        <AppName>
          <MovieImage src='/movie-icon.png'/>
          The Movie Search App
        </AppName> 
        <SearchBox>
          <SearchIcon src='/search-icon1.png'/>
        </SearchBox>
      </Header>
        The Hook
    </Container>
  );
}

export default App;
